import { useEffect, useState } from "react";
import classes from "./FetchData.module.css";
import img from "../image/200w.gif"


const FetchData = () => {
  const [isData,setIsData] = useState([]);
  const [search,setSearch] = useState('');
  const [isLoading,setIsLoading]= useState(false);


  useEffect(() => {
    setIsLoading(true);
    setTimeout(() => {
      fetch("https://jsonplaceholder.typicode.com/users")
      .then((respose) => {
        return respose.json();
      })
      .then((data) => {
        setIsData(data);
        setIsLoading(false);
        console.log(data);
      });
    }, 2000);
  }, []);

  const changeHandler = (event)=>{
    setIsLoading(true)
   setTimeout(() => {
    setSearch(event.target.value)
   },2000
   ); 
    setIsLoading(false)
}


  return (
    <div>
      <section className={classes.section}>
        <input type="text" placeholder="Search" className={classes.input} onChange={changeHandler} />
      </section>
      <section className={classes.section}>
        <table className={classes.table}>
          <thead>
            <tr>
              <th>Name</th>
              <th>Username</th>
              <th>Email</th>
              <th>Mobile</th>
            </tr>
          </thead>
          {isLoading && <img src={img} className={classes.img}/>}

          {isData.filter((data)=>{
            return search.toLowerCase() === '' ? data: data.name.toLowerCase().includes(search) || data.username.toLowerCase().includes(search) || data.email.toLowerCase().includes(search) || data.phone.toLowerCase().includes(search)}).map((data) => {
            return (
              <tbody key={data.id}>
                <tr>
                  <td>{data.name}</td>
                  <td>{data.username}</td>
                  <td>{data.email}</td>
                  <td>{data.phone}</td>
                </tr>
              </tbody>
            );
          }) }
        </table>
      </section>
    </div>
  );
};
export default FetchData;
